import Vue from 'vue'
import Router from 'vue-router'


import Home from './views/Home'
import About from './views/About'


Vue.use(Router)  //플러그인 등록

const router = new VueRouter({
    mode : 'history',
    routes : [
        //각 path 마다 연결시킬 component를 router를 통해 설정
        //router의 사용 선언은 main.js에서 해줄예정
        {
            path : '/',
            component : Home
        },
        {
            path : '/about',
            component : About
        }
    ]
}) 



import stylePage    from '@/views/stylePage.vue'
import notiPage    from '@/components/notification.vue'




Vue.use(Router)  //플러그인 등록

export default new Router({
  mode: 'history',
  routes: [
    {
      path: '/',
      redirect: '/style',
    },

    {
      path: '/style',
      component: stylePage,
    },
    {
      path : '/notifications',
      component: notiPage
    }

  ]
})
